mapper.select:
SELECT COUNT(1)
FROM user_bond
WHERE bond_code = #{bondCode} AND user_id = #{userId}

mapper.update:
UPDATE user_bond
SET bond_name = #{bond.bondName},
    average_purchase_price = #{bond.averagePurchasePrice},
    quantity = #{bond.quantity},
    interest_rate = #{bond.interestRate}
WHERE bond_code = #{bond.bondCode} AND user_id = #{userId}

mapper.select:
SELECT bond_code
FROM user_bond
WHERE user_id = #{userId}
